import React, { useState, useEffect, useCallback, useRef } from 'react';
import ReactFlow, { addEdge, MiniMap, Controls, Background, ReactFlowProvider, Handle, applyNodeChanges, applyEdgeChanges, ConnectionLineType, Panel } from 'reactflow';
import Sidebar from './Sidebar';
import PropertiesPanel from './PropertiesPanel'; // Import the new PropertiesPanel
import { v4 as uuidv4 } from 'uuid';
import axios from 'axios';
import './App.css';

const initialNodes = [];
const initialEdges = [];

function App() {
  const [nodes, setNodes] = useState(initialNodes);
  const [selectedNodeId, setSelectedNodeId] = useState(null);
  const [edges, setEdges] = useState(initialEdges);
  const [workflows, setWorkflows] = useState([]);
  const [selectedWorkflow, setSelectedWorkflow] = useState(null);
  const [isDragOver, setIsDragOver] = useState(false);
  const reactFlowWrapper = useRef(null);

  useEffect(() => {
    axios.get('/api/workflows').then(res => setWorkflows(res.data));
  }, []);

  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), []);
  const [reactFlowInstance, setReactFlowInstance] = useState(null);
  const [connectingNodeId, setConnectingNodeId] = useState(null);

  const onConnectStart = useCallback((_, { nodeId, handleType }) => {
    if (handleType === 'source') {
      setConnectingNodeId(nodeId);
    }
  }, []);

  const onConnectStop = useCallback(() => {
    setConnectingNodeId(null);
  }, []);

  const onConnectEnd = useCallback(
    (event) => {
      if (!event.target || !connectingNodeId) return;

      const targetIsPane = event.target.classList.contains('react-flow__pane');

      if (targetIsPane) {
        let position = { x: 0, y: 0 };
        if (reactFlowInstance) {
             position = reactFlowInstance.project({ x: event.clientX, y: event.clientY });
        } else {
             const { top, left } = reactFlowWrapper.current.getBoundingClientRect();
             position = {
               x: event.clientX - left,
               y: event.clientY - top,
             };
        }

        const newNode = {
          id: uuidv4(),
          position,
          data: { label: 'New node' },
          type: 'operator',
        };

        setNodes((nds) => nds.concat(newNode));
        setEdges((eds) =>
          eds.concat({ id: uuidv4(), source: connectingNodeId, target: newNode.id })
        );
      }
      setConnectingNodeId(null);
    },
    [connectingNodeId, reactFlowInstance]
  );

  // Enhanced drag and drop handlers
  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
    setIsDragOver(true);
  }, []);

  const onDragLeave = useCallback((event) => {
    event.preventDefault();
    if (!event.currentTarget.contains(event.relatedTarget)) {
      setIsDragOver(false);
    }
  }, []);

  const onDrop = useCallback(
    (event) => {
      event.preventDefault();
      setIsDragOver(false);

      const type = event.dataTransfer.getData('application/reactflow');

      if (!type || !reactFlowInstance || !reactFlowWrapper.current) {
        console.error('Drop failed: missing data or React Flow not ready');
        return;
      }

      try {
        const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();

        // Calculate position relative to the React Flow pane
        const position = reactFlowInstance.project({
          x: event.clientX - reactFlowBounds.left,
          y: event.clientY - reactFlowBounds.top,
        });

        const newNodeId = uuidv4();
        const newNode = {
          id: newNodeId,
          type,
          position,
          data: { label: `${type}` },
          draggable: true,
          selectable: true,
          deletable: true,
        };

        setNodes((nds) => [...nds, newNode]);

        // If there's a selected node, create an edge from it to the new node
        if (selectedNodeId) {
          const newEdge = {
            id: `e${selectedNodeId}-${newNodeId}`,
            source: selectedNodeId,
            target: newNodeId,
            type: 'smoothstep',
          };

          setEdges((eds) => addEdge(newEdge, eds));
        }

        // Auto-select the newly created node
        setSelectedNodeId(newNodeId);

        console.log(`Successfully dropped ${type} node at position:`, position);
      } catch (error) {
        console.error('Error during drop:', error);
        alert('Failed to drop node. Please try again.');
      }
    },
    [reactFlowInstance, selectedNodeId]
  );

  const saveWorkflow = () => {
    const payload = {
      id: selectedWorkflow?.id,
      name: selectedWorkflow?.name || 'New Workflow',
      data: { nodes, edges },
    };
    axios.post('/api/workflows', payload).then(res => {
      setSelectedWorkflow(res.data);
      setWorkflows(list => {
        const exists = list.find(w => w.id === res.data.id);
        if (exists) return list.map(w => w.id === res.data.id ? res.data : w);
        return [...list, res.data];
      });
    });
  };

  const loadWorkflow = wf => {
    setSelectedWorkflow(wf);
    setNodes(wf.data?.nodes || []);
    setEdges(wf.data?.edges || []);
  };

  const deleteWorkflow = (id) => {
    axios.delete(`/api/workflows/${id}`).then(() => {
      setWorkflows(list => list.filter(wf => wf.id !== id));
      if (selectedWorkflow && selectedWorkflow.id === id) {
        setSelectedWorkflow(null);
        setNodes([]);
        setEdges([]);
      }
    }).catch(err => {
      console.error('Failed to delete workflow:', err);
      alert('Failed to delete workflow. Please check the console.');
    });
  };

  // Custom node renderer
  const nodeTypes = {
    subscriber: ({ data, id, ...rest }) => (
      <div
        {...rest}
        style={{
          padding: 12,
          borderRadius: 8,
          background: '#fff',
          border: '2px solid #1976d2',
          minWidth: 120,
          maxWidth: 120,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          boxShadow: '0 2px 8px rgba(25,118,210,0.08)',
          cursor: 'grab',
          userSelect: 'none'
        }}
        onClick={() => setSelectedNodeId(id)}
        onMouseDown={(e) => e.stopPropagation()}
      >
        <span style={{ fontSize: 20, marginBottom: 2 }}>👤</span>
        <span style={{ fontWeight: 'bold', fontSize: '12px' }}>Subscriber</span>
        {data.selected && <span style={{ fontSize: '10px', color: '#555', marginTop: 2 }}>{data.selected}</span>}
        <Handle
          type="source"
          position="right"
          style={{ background: '#1976d2', width: 8, height: 8, right: -4 }}
          isConnectable={true}
        />
        <Handle
          type="target"
          position="left"
          style={{ background: '#1976d2', width: 8, height: 8, left: -4 }}
          isConnectable={true}
        />
      </div>
    ),
    operator: ({ data, id, ...rest }) => (
      <div
        {...rest}
        style={{
          padding: 12,
          borderRadius: 8,
          background: '#fff',
          border: '2px solid #d97706',
          minWidth: 120,
          maxWidth: 120,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          boxShadow: '0 2px 8px rgba(217,119,6,0.08)',
          cursor: 'grab',
          userSelect: 'none'
        }}
        onClick={() => setSelectedNodeId(id)}
        onMouseDown={(e) => e.stopPropagation()}
      >
        <span style={{ fontSize: 20, marginBottom: 2 }}>⚙️</span>
        <span style={{ fontWeight: 'bold', fontSize: '12px' }}>Operator</span>
        {data.selected && <span style={{ fontSize: '10px', color: '#555', marginTop: 2 }}>{data.selected}</span>}
        <Handle
          type="source"
          position="right"
          style={{ background: '#d97706', width: 8, height: 8, right: -4 }}
          isConnectable={true}
        />
        <Handle
          type="target"
          position="left"
          style={{ background: '#d97706', width: 8, height: 8, left: -4 }}
          isConnectable={true}
        />
      </div>
    ),
    action: ({ data, id, ...rest }) => (
      <div
        {...rest}
        style={{
          padding: 12,
          borderRadius: 8,
          background: '#fff',
          border: '2px solid #d32f2f',
          minWidth: 120,
          maxWidth: 120,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          boxShadow: '0 2px 8px rgba(211,47,47,0.08)',
          cursor: 'grab',
          userSelect: 'none'
        }}
        onClick={() => setSelectedNodeId(id)}
        onMouseDown={(e) => e.stopPropagation()}
      >
        <span style={{ fontSize: 20, marginBottom: 2 }}>🚀</span>
        <span style={{ fontWeight: 'bold', fontSize: '12px' }}>Action</span>
        {data.selected && <span style={{ fontSize: '10px', color: '#555', marginTop: 2 }}>{data.selected}</span>}
        <Handle
          type="source"
          position="right"
          style={{ background: '#d32f2f', width: 8, height: 8, right: -4 }}
          isConnectable={true}
        />
        <Handle
          type="target"
          position="left"
          style={{ background: '#d32f2f', width: 8, height: 8, left: -4 }}
          isConnectable={true}
        />
      </div>
    ),
  };

  return (
    <ReactFlowProvider>
      <div className="app">
        <div className="sidebar">
          <button onClick={() => { setNodes([]); setEdges([]); }}>New</button>
          <button onClick={saveWorkflow}>Save</button>
          <div>
            <h3>Workflows</h3>
            {workflows.map(wf => (
              <div key={wf.id} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '4px 0' }}>
                <span onClick={() => loadWorkflow(wf)} style={{ cursor: 'pointer', flexGrow: 1 }}>
                  {wf.name}
                </span>
                <button onClick={(e) => { e.stopPropagation(); deleteWorkflow(wf.id); }} style={{ marginLeft: '8px', background: '#d32f2f', color: 'white', border: 'none', borderRadius: '4px', padding: '2px 6px', cursor: 'pointer' }}>
                  Delete
                </button>
              </div>
            ))}
          </div>
          <Sidebar />
        </div>
        <div className="canvas-area">
          <div className="canvas-label">Workflow Canvas (Drop nodes here)</div>
          <div 
            className={`canvas ${isDragOver ? 'drag-over' : ''}`} 
            ref={reactFlowWrapper}
            onDragOver={onDragOver}
            onDragLeave={onDragLeave}
            onDrop={onDrop}
          >
            <ReactFlow
              nodes={nodes}
              edges={edges}
              nodeTypes={nodeTypes}
              onConnect={onConnect}
              onNodesChange={(changes) => {
                console.log('onNodesChange called with:', changes);
                setNodes((nds) => applyNodeChanges(changes, nds));
              }}
              onEdgesChange={(changes) => setEdges((eds) => applyEdgeChanges(changes, eds))}
              onInit={setReactFlowInstance}
              onConnectStart={onConnectStart}
              onConnectStop={onConnectStop}
              onConnectEnd={onConnectEnd}
              fitView
              onNodeClick={(_, node) => setSelectedNodeId(node.id)}
              connectionLineType={ConnectionLineType.SmoothStep}
              connectionLineStyle={{ stroke: '#555', strokeWidth: 2 }}
              nodesDraggable={true}
              nodesConnectable={true}
              elementsSelectable={true}
              onNodeDragStop={(event, node) => {
                console.log('Node dragged to:', node.position);
              }}
            >
              <MiniMap />
              <Controls />
              <Background />
            </ReactFlow>
          </div>
          {selectedNodeId && (
            <PropertiesPanel
              node={nodes.find(n => n.id === selectedNodeId)}
              onChange={updatedData => {
                setNodes(nds => nds.map(n => n.id === selectedNodeId ? { ...n, data: { ...n.data, ...updatedData } } : n));
              }}
              onClose={() => setSelectedNodeId(null)}
            />
          )}
        </div>
      </div>
    </ReactFlowProvider>
  );
}

// NodeEditor component is now replaced by PropertiesPanel.js

export default App;
